    "INSERT INTO `products` (`id`, `subcategory_id`, `brand_id`, `name`, `title`, `meta_d`, `meta_k`, `image`, `short_description`, `description`, `price`, `currency`, `visibility`, `status_id`, `sale_price`) VALUES(6067, 174, '155', 'MAGMA CD-Wallet 304 RPM Черный', 'MAGMA CD-Wallet 304 RPM Черный', 'MAGMA CD-Wallet 304 RPM Черный цена купить Папка для CD 304 + 12 дисков, чёрный, идеально подходит для DJs ', 'MAGMA CD-Wallet 304 RPM Черный', 'MAGMA CD-Wallet 304 RPM.jpg', '<p>Папка для CD 304 + 12 дисков, чёрный</p>', '<p>Папка из водонепроницаемого материала на 304+12 CD/DVD. Внутри расположены 12 карманов для первоочередного материала. Работу упрощает двухцветная система закладок. Снаружи имеется карман. Можно переносить с помощью ручки или повесив через плечо с помощью ремня.<br /><br /><strong>Характеристики</strong><br />1680D Ballistic cверхпрочный, водонепроницаемый нейлон<br />Отделение для 304 CD/DVD + 12 дополнительных карманов для предварительно отобранного материала<br />RPM-System: 2х цветная система закладок для быстрого и удобного просмотра рабочего материала<br />Внешний карман<br />Ручка для переноски<br />Съемный ремень через плечо</p>', 58.00, 'usd', true, 2, 0.00);",
